SELECT count(*)
FROM like_hate
WHERE news_id = #{newsId} AND uid = #{uid};

mapper.update:
UPDATE like_hate
SET like_todo = (like_todo + 1) % 2
WHERE news_id = #{newsId} AND uid = #{uid}

